import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000";

// 백엔드 응답 포맷 정규화 (키 이름이 다른 경우를 흡수)
function normalizeResult(data) {
  if (!data) return null;

  // 이미지 키 정리
  const original =
    data.images?.original || data.original_b64 || data.original || null;
  const overlay =
    data.images?.overlay || data.overlay_b64 || data.overlay || null;

  // 확률/메타 정리
  const predLabel = data.pred_label ?? data.label ?? null;
  const predProb =
    typeof data.pred_prob === "number"
      ? data.pred_prob
      : typeof data.prob === "number"
      ? data.prob
      : null;

  const threshold = data.threshold_used ?? data.threshold ?? null;
  const targetLayer = data.target_layer_used ?? data.used_layer_name ?? null;

  const runtimeMs = data.runtime_ms ?? data.runtime ?? null;

  return {
    ...data,
    pred_label: predLabel,
    pred_prob: predProb,
    threshold_used: threshold,
    target_layer_used: targetLayer,
    runtime_ms: runtimeMs,
    images: {
      original: original?.startsWith("data:")
        ? original
        : original
        ? `data:image/png;base64,${original}`
        : null,
      overlay: overlay?.startsWith("data:")
        ? overlay
        : overlay
        ? `data:image/png;base64,${overlay}`
        : null,
    },
  };
}

export default function Diagnosis() {
  const location = useLocation();
  const params = useParams();
  const navigate = useNavigate();

  const xrayId = useMemo(() => {
    // 우선순위: state → URL param
    return location?.state?.xrayId || params?.xrayId || null;
  }, [location?.state?.xrayId, params?.xrayId]);

  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");
  const [result, setResult] = useState(null);

  // 진단/소견/LLM/저장 관련 상태 (이전 기능 유지)
  const [diagnosis, setDiagnosis] = useState("");
  const [doctorNotes, setDoctorNotes] = useState("");
  const [llmSummary, setLlmSummary] = useState("");
  const [saving, setSaving] = useState(false);
  const [savedData, setSavedData] = useState(null);

  useEffect(() => {
    if (!xrayId) {
      setErrorMsg(
        "xrayId가 전달되지 않았습니다. 리스트의 ‘열기’로 진입하거나 /diagnosis/:xrayId 경로를 사용하세요."
      );
      return;
    }

    const analyze = async () => {
      setLoading(true);
      setErrorMsg("");
      setResult(null);
      setDiagnosis("");
      setDoctorNotes("");
      setLlmSummary("");
      setSavedData(null);

      try {
        // 1) POST /api/analyze-by-id { xrayId }
        let res = await fetch(`${API_BASE}/api/analyze-by-id`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ xrayId }),
        });

        // 2) 실패 시 GET /api/analyze?xrayId=...
        if (!res.ok) {
          res = await fetch(
            `${API_BASE}/api/analyze?xrayId=${encodeURIComponent(xrayId)}`
          );
        }

        const dataRaw = await res.json().catch(() => null);
        if (!res.ok || !dataRaw) {
          throw new Error(
            dataRaw?.error || `분석 요청 실패 (HTTP ${res.status})`
          );
        }

        const data = normalizeResult(dataRaw);
        setResult(data);

        // 기본 진단 문구 프리필
        if (data?.pred_label) {
          setDiagnosis(
            `모델 판독: ${data.pred_label}` +
              (typeof data.pred_prob === "number"
                ? ` (P=${data.pred_prob.toFixed(4)})`
                : "")
          );
        }
      } catch (err) {
        setErrorMsg(err?.message || "분석 중 오류가 발생했습니다.");
      } finally {
        setLoading(false);
      }
    };

    analyze();
  }, [xrayId]);

  const fmtProbPct = (p) => {
    if (p === null || p === undefined) return "-";
    const v = Number(p);
    if (Number.isNaN(v)) return "-";
    return `${(v * 100).toFixed(1)}%`;
  };

  const handleLLMSummarize = async () => {
    if (!result) {
      setErrorMsg("먼저 분석이 완료되어야 합니다.");
      return;
    }
    setErrorMsg("");
    setLlmSummary("요약 생성 중…");
    try {
      const res = await fetch(`${API_BASE}/api/llm-summarize`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          pred_label: result.pred_label,
          pred_prob: result.pred_prob,
          image_data_url: result.images?.original, // 원본 이미지 dataURL
        }),
      });
      const data = await res.json().catch(() => null);
      if (!res.ok || !data?.ok) {
        throw new Error(data?.error || `HTTP ${res.status}`);
      }
      setLlmSummary(data.summary || "");
      if (!doctorNotes) setDoctorNotes(data.summary || "");
    } catch (err) {
      setErrorMsg(err?.message || "LLM 요약 중 오류가 발생했습니다.");
      setLlmSummary("");
    }
  };

  const handleSave = async () => {
    if (!diagnosis || !doctorNotes) {
      setErrorMsg("진단 내용과 의사 소견을 모두 입력해 주세요.");
      return;
    }
    setSaving(true);
    setErrorMsg("");

    try {
      const res = await fetch(`${API_BASE}/api/update-diagnosis`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          xray_id: xrayId, // 식별자도 함께 저장 권장
          diagnosis,
          doctor_notes: doctorNotes,
          meta: {
            pred_label: result?.pred_label,
            pred_prob: result?.pred_prob,
            threshold_used: result?.threshold_used,
            target_layer_used: result?.target_layer_used,
            runtime_ms: result?.runtime_ms,
          },
        }),
      });
      const data = await res.json().catch(() => null);
      if (!res.ok || !data?.ok) {
        throw new Error(data?.error || `HTTP ${res.status}`);
      }
      setSavedData(data.data);
      alert("진단 내용이 성공적으로 저장되었습니다.");
    } catch (err) {
      setErrorMsg(err?.message || "저장 중 오류가 발생했습니다.");
    } finally {
      setSaving(false);
    }
  };

  const handleLoadSavedData = () => {
    if (savedData) {
      setDiagnosis(savedData.diagnosis || "");
      setDoctorNotes(savedData.doctor_notes || "");
    } else {
      setErrorMsg("저장된 데이터가 없습니다.");
    }
  };

  const backToList = () => {
    navigate(-1);
  };

  return (
    <div className="bg-[#1a202c] text-white p-8 rounded-lg min-h-screen">
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold">자동 판독 · Grad-CAM 결과</h1>
        <div className="flex gap-2">
          <button
            onClick={() => window.location.reload()}
            className="bg-cyan-600 hover:bg-cyan-500 text-white font-semibold px-4 py-2 rounded-lg"
          >
            다시 분석
          </button>
          <button
            onClick={backToList}
            className="bg-gray-700 hover:bg-gray-600 text-white font-semibold px-4 py-2 rounded-lg"
          >
            목록으로
          </button>
        </div>
      </div>

      <div className="mb-3 text-gray-300 text-sm">
        <span className="text-gray-400">X-ray ID:</span>{" "}
        <span className="font-mono">{xrayId ?? "(없음)"}</span>
      </div>

      {loading && (
        <div className="w-full py-24 flex flex-col items-center justify-center gap-3">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-cyan-400"></div>
          <p className="text-gray-400">분석 중입니다…</p>
        </div>
      )}

      {!loading && errorMsg && (
        <div className="bg-red-900/40 border border-red-700 rounded-lg p-4 text-red-200">
          <p className="font-semibold mb-1">오류</p>
          <p className="text-sm leading-relaxed">{errorMsg}</p>
        </div>
      )}

      {!loading && !errorMsg && result && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* 결과 이미지 */}
          <div className="lg:col-span-2 bg-[#2d3748] rounded-xl p-6 shadow">
            <h2 className="text-lg font-semibold mb-4">영상</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <div className="text-sm text-gray-300 mb-2">Original</div>
                {result.images?.original ? (
                  <img
                    src={result.images.original}
                    alt="original"
                    className="rounded-lg w-full"
                  />
                ) : (
                  <div className="text-sm text-gray-400">
                    원본 이미지가 없습니다.
                  </div>
                )}
              </div>
              <div>
                <div className="text-sm text-gray-300 mb-2">
                  Grad-CAM Overlay
                </div>
                {result.images?.overlay ? (
                  <img
                    src={result.images.overlay}
                    alt="overlay"
                    className="rounded-lg w-full"
                  />
                ) : (
                  <div className="text-sm text-gray-400">
                    Grad-CAM 이미지가 없습니다.
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* 메타/진단/소견 */}
          <div className="bg-[#2d3748] rounded-xl p-6 shadow">
            <h2 className="text-lg font-semibold mb-4">결과 요약</h2>

            <div className="grid grid-cols-2 gap-3 text-sm mb-5">
              <div className="bg-[#1f2733] p-3 rounded">
                <div className="text-gray-300">Pred Label</div>
                <div
                  className={`font-semibold ${
                    result.pred_label === "PNEUMONIA"
                      ? "text-red-300"
                      : result.pred_label === "NORMAL"
                      ? "text-green-300"
                      : "text-cyan-300"
                  }`}
                >
                  {result.pred_label ?? "-"}
                </div>
              </div>
              <div className="bg-[#1f2733] p-3 rounded">
                <div className="text-gray-300">Pred Prob</div>
                <div className="font-semibold">
                  {fmtProbPct(result.pred_prob)}
                </div>
              </div>
              <div className="bg-[#1f2733] p-3 rounded">
                <div className="text-gray-300">Threshold</div>
                <div className="font-semibold">
                  {result.threshold_used ?? "-"}
                </div>
              </div>
              <div className="bg-[#1f2733] p-3 rounded">
                <div className="text-gray-300">Grad-CAM Layer</div>
                <div className="font-semibold">
                  {result.target_layer_used ?? "-"}
                </div>
              </div>
            </div>

            <div className="mb-4">
              <label className="block text-sm text-gray-300 mb-2">
                진단 결과
              </label>
              <input
                type="text"
                value={diagnosis}
                onChange={(e) => setDiagnosis(e.target.value)}
                className="w-full bg-[#1a202c] border border-gray-600 rounded px-4 py-2 text-sm"
                placeholder="예) 모델 판독: PNEUMONIA (P=0.8732)"
              />
            </div>

            <div className="mb-4">
              <div className="flex items-center justify-between mb-2">
                <label className="block text-sm text-gray-300">의사 소견</label>
                <button
                  onClick={handleLLMSummarize}
                  className="text-xs bg-indigo-600 hover:bg-indigo-700 px-3 py-1 rounded"
                  title="LLM으로 요약 초안 생성"
                >
                  LLM 요약 만들기
                </button>
              </div>
              {!!llmSummary && (
                <div className="text-xs text-gray-300 bg-[#1f2733] p-3 rounded mb-2 whitespace-pre-wrap">
                  {llmSummary}
                </div>
              )}
              <textarea
                value={doctorNotes}
                onChange={(e) => setDoctorNotes(e.target.value)}
                className="w-full bg-[#1a202c] border border-gray-600 rounded px-4 py-2 text-sm"
                rows="5"
                placeholder="의사 소견을 입력하세요… (LLM 초안을 참고하여 수정)"
              />
            </div>

            <div className="flex gap-3">
              <button
                onClick={handleSave}
                disabled={saving}
                className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded-lg font-semibold disabled:opacity-60"
              >
                {saving ? "저장 중…" : "저장하기"}
              </button>
              <button
                onClick={handleLoadSavedData}
                className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-lg font-semibold"
              >
                저장된 데이터 불러오기
              </button>
              <button
                onClick={() =>
                  navigate(`/diagnosis/${xrayId}`, { replace: true })
                }
                className="bg-gray-700 hover:bg-gray-600 px-4 py-2 rounded-lg font-semibold"
                title="주소를 /diagnosis/:xrayId 형태로 고정"
              >
                주소 고정
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
