import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";

import Main1 from "./pages/Main1";
import Main2 from "./pages/Main2";
import Navbar from "./components/Navbar";
import Login from "./components/login/Login";
import Logout from "./components/login/Logout";
import SignUp from "./components/login/SignUp";
import FindAccount from "./components/login/FindAccount";
import Notice from "./pages/Notice";
import MemberManagement from "./components/login/MemberManagement";
import XRayUpload from "./pages/XRayUpload";
import Profile from "./components/login/Profile";
import DiagnosisList from "./pages/DiagnosisList";
import UploadHistory from "./pages/UploadHistory";
import Diagnosis from "./components/diagnosis/Diagnosis";
import { AuthProvider } from "./components/layout/AuthProvider";

function App() {
  const [currentPage, setCurrentPage] = useState("main");
  const [currentUser, setCurrentUser] = useState(null);
  const [selectedXrayId, setSelectedXrayId] = useState(null);

  useEffect(() => {
    const storedUserId = localStorage.getItem("userId");
    const storedUserName = localStorage.getItem("userName");
    const storedRoleCd = localStorage.getItem("roleCd");
    const storedEmail = localStorage.getItem("email");
    const isLogin = localStorage.getItem("isLogin");

    if (isLogin && storedUserId && storedUserName && storedRoleCd) {
      setCurrentUser({
        memberId: storedUserId,
        memberName: storedUserName,
        role: storedRoleCd,
        email: storedEmail,
      });
    }
  }, []);

  const handleLogin = (userData) => {
    setCurrentUser(userData);
    setCurrentPage("main");
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setCurrentPage("main");
  };

  const handleNavigate = (page, payload) => {
    setCurrentPage(page);
    if (payload && payload.xrayId) setSelectedXrayId(payload.xrayId);
  };

  const renderPage = () => {
    if (!currentUser) {
      switch (currentPage) {
        case "login":
          return <Login onLogin={handleLogin} onNavigate={handleNavigate} />;
        case "signup":
          return <SignUp onNavigate={handleNavigate} />;
        case "find-account":
          return <FindAccount onNavigate={handleNavigate} />;
        case "main":
        default:
          return <Main1 />;
      }
    }

    switch (currentPage) {
      case "logout":
        return <Logout onNavigate={handleNavigate} onLogout={handleLogout} />;
      case "notice":
        return <Notice currentUser={currentUser} />;
      case "profile":
        return (
          <Profile currentUser={currentUser} setCurrentUser={setCurrentUser} />
        );
      case "members":
        return currentUser.role === "A" ? (
          <MemberManagement />
        ) : (
          <h2 className="text-center">접근 권한이 없습니다.</h2>
        );
      case "diagnosis":
        return <XRayUpload currentUser={currentUser} />;
      case "view-diagnosis":
        return (
          <Diagnosis
            xrayId={selectedXrayId}
            currentUser={currentUser}
            onNavigate={handleNavigate}
          />
        );
      case "diagnosis-list":
        return currentUser.role === "D" || currentUser.role === "A" ? (
          <DiagnosisList onNavigate={handleNavigate} />
        ) : (
          <h2 className="text-center">접근 권한이 없습니다.</h2>
        );
      case "xray-upload":
        return currentUser.role === "X" ? (
          <XRayUpload currentUser={currentUser} />
        ) : (
          <h2 className="text-center">접근 권한이 없습니다.</h2>
        );
      case "upload-history":
        return currentUser.role === "X" || currentUser.role === "A" ? (
          <UploadHistory currentUser={currentUser} />
        ) : (
          <h2 className="text-center">접근 권한이 없습니다.</h2>
        );
      case "main":
      default:
        return <Main2 currentUser={currentUser} onNavigate={handleNavigate} />;
    }
  };

  return (
    <AuthProvider>
      <div className="bg-gray-900 text-white min-h-screen font-sans">
        <Navbar
          currentUser={currentUser}
          onLogout={handleLogout}
          onNavigate={handleNavigate}
        />

        {/* 메인 컨테이너 안에 Routes 배치 + 루트/와일드카드에 기본 화면 지정 */}
        <main className="container mx-auto p-4 sm:p-6 md:p-8 pt-24">
          <Routes>
            {/* 루트 경로: 기존 내부 상태 라우팅 화면을 그대로 렌더 */}
            <Route path="/" element={renderPage()} />

            {/* URL로 직접 접근 가능한 화면들 */}
            <Route
              path="/diagnosis/:xrayId"
              element={
                <Diagnosis
                  currentUser={currentUser}
                  onNavigate={handleNavigate}
                />
              }
            />
            <Route
              path="/diagnosis-list"
              element={<DiagnosisList onNavigate={handleNavigate} />}
            />

            {/* 그 외 모든 경로도 기본 화면으로 */}
            <Route path="*" element={renderPage()} />
          </Routes>
        </main>
      </div>
    </AuthProvider>
  );
}

export default App;
